import { Component, Input } from '@angular/core';
import { Observable } from 'rxjs';
import { CartItem } from '../../interfaces/cart.interface';
import { StoreService } from 'src/app/services/store.service';
import { Product } from 'src/app/interfaces/product.interface';

@Component({
  selector: 'app-product-cart',
  templateUrl: './product-cart.component.html',
  styleUrls: ['./product-cart.component.css'],
})
export class ProductCartComponent {
  @Input()
  data!: Observable<CartItem[]>;
  cartItems$: Observable<CartItem[]>;
  total$: Observable<number>;

  constructor(private storeService: StoreService) {
    this.cartItems$ = this.storeService.cartItems$;
    this.total$ = this.storeService.total$;
  }

  removeItemFromCart(product: Product): void {
    this.storeService.removeItemFromCart(product);
  }
}
<div *ngIf="data.l">
  <mat-card fxLayoutAlign="left auto" *ngFor="let item of (data | async)">
    <mat-card-header>
      <img mat-card-sm-image src="{{ item.product.image }}">
      <mat-card-title-group>
        <mat-card-title>{{ item.product.title }}</mat-card-title>
        <mat-card-subtitle>Price: {{ item.product.price }}</mat-card-subtitle>
        <div>
          Quantity: {{ item.quantity }}
          <button (click)="removeItemFromCart(item.product)">Remove</button>
        </div>
      </mat-card-title-group>
    </mat-card-header>
  </mat-card>
</div>
