import { Component, Input, OnInit } from '@angular/core';
// import { HttpClient } from '@angular/common/http';
import { Product } from '../../interfaces/product.interface';
import { StoreService } from '../../services/store.service';
import { Observable } from 'rxjs';
@Component({
  selector: 'app-product-grid',
  templateUrl: './product-grid.component.html',
  styleUrls: ['./product-grid.component.css'],
})
export class ProductGridComponent {
  @Input() gridproducts$: Observable<Product[]> | null = null;

  searchKey: string = '';

  get filteredProducts$(): Observable<Product[]> | null {
    return this.gridproducts$?.pipe(map(products => this.filterProducts(products)));
  }

  private filterProducts(products: Product[]): Product[] {
    if (!this.searchKey) {
      return products;
    }
    return products.filter(product => product.title.toLowerCase().includes(this.searchKey.toLowerCase()));
  }


}
<div class="outerContainer">
    <div fxLayout="row wrap" fxLayoutGap="20px" class="container">
      <app-product-card
        fxLayoutAlign="center center"
        *ngFor="let product of (gridproducts$ | async)?.filter(searchKey, 'title')"
        [data]="product"
      ></app-product-card>
    </div>
  </div>
