import { Injectable } from '@angular/core';
import { ajax } from 'rxjs/ajax';
import { Observable, BehaviorSubject } from 'rxjs';
import { Product } from '../interfaces/product.interface';

@Injectable({
  providedIn: 'root',
})
export class StoreService {
  private cartItemsSubject = new BehaviorSubject<any>([]);
  private totalSubject: BehaviorSubject<number> = new BehaviorSubject<number>(0);
  //never expose subjects directly -> use observable
  cartItems$ = this.cartItemsSubject.asObservable();
  //products observable
  producst$ = ajax.getJSON<Product[]>('/assets/data.json');
  //total observable
  total$ = this.cartItemsSubject.asObservable();

  addItemToCart(product: Product): void {
    this.cartItemsSubject.next([...this.cartItemsSubject.getValue(), product]);
    this.total$ += product.price;
  }
}
