import { Component, Input } from '@angular/core';
import { Observable } from 'rxjs';
import { Product } from '../../interfaces/product.interface';
import { StoreService } from 'src/app/services/store.service';

@Component({
  selector: 'app-product-cart',
  templateUrl: './product-cart.component.html',
  styleUrls: ['./product-cart.component.css'],
})
export class ProductCartComponent {
  @Input()
  data!: Observable<Product[]>;
  cartItems$: Observable<Product[]>;
  total$: Observable<number>;

  constructor(private storeService: StoreService) {
    this.cartItems$ = this.storeService.cartItems$;
    this.total$ = this.storeService.total$;
  }

  removeItemFromCart(product: Product): void {
    this.storeService.removeItemFromCart(product);
  }
}
<div *ngFor="let product of cartItems$ | async">
  <div>{{ product.name }}</div>
  <div>Price: {{ product.price }}</div>
  <button (click)="removeItemFromCart(product)">Remove</button>
</div>
<div>Total: {{ total$ | async }}</div>
