import { Component } from '@angular/core';
import { Product } from '../app/interfaces/product.interface';
import { StoreService } from './services/store.service';
import { Observable } from 'rxjs';
import { CartItem } from './interfaces/cart.interface';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'hw1';

  products$: Observable<Product[]>;
  cartItems$: Observable<CartItem[]>;
  total$: Observable<number>;

  constructor(private storeService: StoreService) {
    this.products$ = this.storeService.products$;
    this.cartItems$ = this.storeService.cartItems$;
    this.total$ = this.storeService.total$;
  }

  addItemToCart(product: Product): void {
    this.storeService.addItemToCart(product);
  }
}

<div class="main">
  <div class="catalogue">
    <h1>Online Shop</h1>
    <app-product-grid [gridproducts$]="products$"></app-product-grid>
  </div>
  <div class="cart">
    <h1>Shopping Cart</h1>
    <app-product-cart [data]="cartItems$"></app-product-cart>
    <p>Total: {{ total$ | async | currency }}</p>
  </div>
</div>
