import { Component, Input } from '@angular/core';
import { Product } from '../../interfaces/product.interface';
import { StoreService } from '../../services/store.service';

@Component({
  selector: 'app-product-card',
  templateUrl: './product-card.component.html',
  styleUrls: ['./product-card.component.css'],
})
export class ProductCardComponent {
  @Input()
  data!: Product;

  constructor(private storeService: StoreService) {}

  addToCart(): void {
    this.storeService.addItemToCart(this.data);
  }
}


  <section class="card">
    <div class="product-image">
      <img src="{{ data.image }}"  draggable="false" />
    </div>
    <div class="product-info">
      <h2>{{ data.title }}</h2>

      <div class="price">price: ${{ data.price }}</div>
    </div>
    <div class="btn">
      <button class="buy-btn" (click)="addToCart()">Buy Now</button>
      <button class="fav">
        <svg class="svg" id="i-star" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 32 32" stroke="#000" stroke-linecap="round" stroke-linejoin="round" stroke-width="2">
          <path d="M16 2 L20 12 30 12 22 19 25 30 16 23 7 30 10 19 2 12 12 12 Z" />
        </svg>
      </button>
    </div>
  </section>
